    var homepageModal = {
		init: function () {
			var that = this;
            if ($('.js-home-modal').length && that.getCookie('modalShown') != true) {
				this.show();
				this.triggerClose();
            }
        },
        show: function() {
            var triggered = false
            var triggerPoint = 1000

            window.addEventListener('scroll', () => {
                if (window.scrollY >= triggerPoint && !triggered) {
                    fullscreenOverlay.open()
                    $('.js-home-modal').css('display', 'flex').hide().fadeIn()
					triggered = true;
                }
            })
        },
        close: function() {
            $('.js-home-modal').fadeOut()
            fullscreenOverlay.close()
        },
        triggerClose: function() {
			$('.js-close-home-modal').click(() => {

				//set cookie
				var d = new Date()
				d.setTime(d.getTime() + (30*24*60*60*1000))
				document.cookie = "modalShown=true; expires="+ d.toUTCString() + "; path=/"

                homepageModal.close()
            })
		},
		getCookie : function(name) {
			var re = new RegExp(name, 'g')
			var count = (document.cookie.match(re) || []).length
			return count
		}
    }

